import React, { useState, useEffect } from "react";
import { readDeck } from "../utils/api";
import { useParams, useHistory, Link } from "react-router-dom";

 const Study = () => {
    const { deckId } = useParams();
    const [ deck, setDecks ]  = useState([]);
    const [ cards, setCards ] = useState([]);
    const [ isFront, setIsFront ] = useState(true);
    const [ cardNumber, setCardNumber ]  = useState(1);
    const history = useHistory();

    //First fetch the data and load the deck
    useEffect(() => {
        async function fetchData() {
            const abortController = new AbortController();
            try {
                //You must use the readDeck() function from src/utils/api/index.js to load the deck that is being studied.
                const data = await readDeck(deckId, abortController.signal);
                setDecks(data);
            } catch(error) {
                console.error("error", error);
            }
            return () => {
                abortController.abort();
            };
        }
        fetchData();
    }, []);

    /*

    */
    function flipCard(){
        if(isFront){
            setIsFront(false);
        }
        else {
            setIsFront(true);
        }
    }

     //this will happen if the next button is clicked on a card
    function nextCard(index){
        const restartDeckConfirmed = window.confirm("Restart cards? Click 'cancel' to return to the homepage");
        if(index < cards.length){
            setCardNumber(cardNumber+1);
            setIsFront(true);
        } else {
            if(restartDeckConfirmed){
                setCardNumber(1);
                setIsFront(true);
            }
            else {
                history.push("/");
            }
        }
    }

    const notEnoughCards = (
        <>
            <p>Not enough cards</p>
            <button>Add Cards</button>
        </>

    )

     /*
    There is a breadcrumb navigation bar with links to home /, followed by the name of the deck being studied, 
    and finally the text Study (e.g., Home/Rendering In React/Study).
    */
    const breadcrumb = (
        <nav>
            <ol className="breadcrumb">
                <li className="breadcrumb-item">
                    <Link to="/">Home</Link>
                </li>
                <li className="breadcrumb-item">
                    <Link to={`/decks/${deck.id}`}>{deck.name}</Link>
                </li>
                <li className="breadcrumb-item">
                    Study
                </li>
            </ol>
        </nav>
    )

    if(cards.length <= 2){
        return (
            <>
            {breadcrumb}
            {notEnoughCards}
            </>
        )
    }
    else{
        return (
            <>
            {/*The deck title (i.e., "Study: Rendering in React" ) is shown on the screen.*/}
            <title>${deck.title}</title>
                <p>You got to the study page</p>
                { cards.map((card) => (
                    <div key={card.id}>
                        <h4>{card.id} of {cards.length}</h4>
                        <p>{card.front}</p>
                        <button>Flip</button>
                        <button>Next</button>
                    </div>
                ))}
            </>
        )
    }
 }


 export default Study;
