import React, { useState, useEffect } from "react";
import { readDeck } from "../utils/api";
import { useParams, useHistory, Link } from "react-router-dom";

 const Study = () => {
    const { deckId } = useParams();
    const [ deck, setDecks ]  = useState([]);
    //const [ cards, setCards ] = useState([]);
    const [ isFront, setIsFront ] = useState(true);
    //note: useState initializes a state variable named numCards with an initial value of 0 and setNumCards is a setter function used to update the value of numCards
    const [ numCards, setNumCards ]  = useState(0); 
    const history = useHistory();

    //First fetch the data and load the deck
    useEffect(() => {
        async function fetchData() {
            const abortController = new AbortController();
            try {
                //You must use the readDeck() function from src/utils/api/index.js to load the deck that is being studied.
                const deckData = await readDeck(deckId, abortController.signal);
                //note: since numCards is a state variable, we can use it anywhere outside of useEffect
                const numCards = deckData.cards.length; //getting the number of cards and setting it to variable
                setNumCards(numCards); //setting the state of the number of cards with numCards above
                setDecks(deckData); //set the data to the state
            } catch(error) {
                console.error("error", error);
            }
            return () => {
                abortController.abort();
            };
        }
        fetchData();
        //note: deckId is dependency array and means that the effect (fetchData) function will only run when the value of deckId changes
        //note: if deckId remains the same between renders, the effect will not be re-executed
    }, [deckId]);

    /*

    */
    function flipCard(){
        if(isFront){
            setIsFront(false);
        }
        else {
            setIsFront(true);
        }
    }

     //this will happen if the next button is clicked on a card
    function nextCard(index){
        const restartDeckConfirmed = window.confirm("Restart cards? Click 'cancel' to return to the homepage");
        if(index < numCards){
            setNumCards(numCards+1);
            setIsFront(true);
        } else {
            if(restartDeckConfirmed){
                setNumCards(1);
                setIsFront(true);
            }
            else {
                history.push("/");
            }
        }
    }

    const notEnoughCards = (
        <div>
            <p>Not enough cards</p>
            <button>Add Cards</button>
        </div>
    )

     /*
    There is a breadcrumb navigation bar with links to home /, followed by the name of the deck being studied, 
    and finally the text Study (e.g., Home/Rendering In React/Study).
    */
    const breadcrumb = (
        <nav>
            <ol className="breadcrumb">
                <li className="breadcrumb-item">
                    <Link to="/">Home</Link>
                </li>
                <li className="breadcrumb-item">
                    <Link to={`/decks/${deck.id}`}>{deck.name}</Link>
                </li>
                <li className="breadcrumb-item">
                    Study
                </li>
            </ol>
        </nav>
    )

    if(numCards <= 2){
        console.log('number of cards (cards.length)', numCards);
        return (
            <>
                {breadcrumb}
                {notEnoughCards}
            </>
        )
    }
    else{
        return (
            <>
            {/*The deck title (i.e., "Study: Rendering in React" ) is shown on the screen.*/}
            {breadcrumb}
            <title>${deck.title}</title>
                <p>You got to the study page</p>
                { deck.map((card) => (
                    <div key={card.id}>
                        <h4>{card.id} of {numCards}</h4>
                        <p>{card.front}</p>
                        <button>Flip</button>
                        <button>Next</button>
                    </div>
                ))}
            </>
        )
    }
 }


 export default Study;
