import React, { useState , useEffect } from "react";
import { readDeck } from "../utils/api";
import { Link, useParams, useHistory } from "react-router-dom";
import CardsList from "./CardsList";
import { deleteDeck } from "../utils/api";


const Deck = () => {
    const deckId = useParams();
    const [ deck, setDeck ] = useState({ cards: []});
    const history = useHistory();

    useEffect(() => {
        const abortController = new AbortController();
        const fetchDeck = async() => {
            try {
                const response = await readDeck(deckId, abortController.signal);
                const data = await response.json();
                setDeck(data);
                console.log('deck data', data);
            }
            catch(error){
                console.log('Error fetching deck', error);
            }
        }
        fetchDeck();
        return () => {
            abortController.abort();
        }
    }, [deckId]);

    const handleEditButton = (deckId) => {
        history.push(`/decks/${deckId}/edit`);
    }

    const handleStudyButton = (deckId) => {
        history.push(`/decks/${deckId}/study`);
    }

    const handleAddCardsButton = (deckId) => {
        history.push(`/decks/${deckId}/cards/new`);
    }

    const handleDeleteButton = async (deckId) => {
        const confirmed = window.confirm('Delete this deck? You will not be able to recover it');
        if (confirmed) {
            try {
                await deleteDeck(deckId);
               // setDecks((prevDecks) => prevDecks.filter((deck) => deck.id !== deckId));
                history.go(0);
            } catch(error){
                console.error("Error deleting deck", error);
            }
        }
    };

    const navBar = (
        <nav>
            <ol className="breadcrumb">
                <li className="breadcrumb-item">
                    <Link to="/">Home</Link>
                </li>
                <li className="breadcrumb-item">
                    {deck.name}
                </li>
            </ol>
        </nav>
    )

    return (
        <div>
            <div>
                {navBar}
                <h3>{deck.name}</h3>
                <p>{deck.description}</p>
                <button onClick={() => handleEditButton(deck.id)}>Edit</button>
                <button onClick={() => handleStudyButton(deck.id)}>Study</button>
                <button onClick={() => handleAddCardsButton(deck.id)}>Add Cards</button> 
                <button onClick={() => handleDeleteButton(deck.id)}>Delete</button>
            </div>
            <div>
                <CardsList deck={deck} setDeck={setDeck}/>
            </div>
        </div>
    )
}

export default Deck;

