import React, { useState, useEffect, useHistory } from "react";
import { Link } from "react-router-dom";
import { deleteDeck, listDecks } from "../utils/api";


const Home = () => {

    const [decks, setDecks] = useState([]);
    const history = useHistory();

    useEffect(() => {
        // Fetch and update decks when the component mounts
        const fetchData = async () => { // This function inside uesEffect will be executed after the component has been rendered
            const data = await listDecks(); // Call utility function listDecks() which makes an asynchronous request to API to get a list of decks; await keyword is used to wait for the promise to resolve
            setDecks(data || []); 
        }; // if data is truthy (API call was successful and it returned data) then set the state to 'data', if it is falsy, set the state to empty array []

        fetchData(); // Invoked when component mounts (ensures that the decks are fetched and the component state is updated with the latest data when the component is first rendered)
    }, []); // [] is the dependency sarray and it is saying that the effect will only run once when the component mounts

    const handleStudyClick = (deckId) => {
        history.push(`/decks/${deckId}/study`);
    }
    /** 
    * Delete Deck prompt
    * When the user clicks the Delete button, a warning message is shown and the user can click OK or Cancel. 
    * If the user clicks OK, the deck is deleted and the deleted deck is no longer visible on the Home screen.
    **/
    const handleDelete = (deckId) => {
        const confirmed = window.confirm('Delete this deck? You will not be able to recover it');
        if (confirmed) {
            deleteDeck(deckId);
            setDecks(decks.filter((deck) => deck.id !== deckId));
        }
    }

  return (
    <>
        {/* A Create Deck button is shown, and clicking it brings the user to the Create Deck screen. */}
        <Link to="/decks/new">
            <button>Create Deck</button>
        </Link>
        {/*Existing decks are each shown with the deck name, the number of cards, and a Study, View, and Delete button.*/}
        { decks.map((deck) => (
            <div key={ deck.id }>
              <Link to={`/decks/${deck.id}`}>Deck {deck.id}</Link>
              {/* Clicking the Study button brings the user to the Study screen. */}
              <Link to={`/decks/${deck.id}/study`}>
                <button>Study</button>
                </Link>
              {/*Clicking the View button brings the user to the Deck screen.*/}
              <Link to={`/decks/${deck.id}`}>
                <button>View</button>
                </Link>
              {/*Clicking the Delete button shows a warning message before deleting the deck.*/}
              <button name="delete" onClick={() => handleDelete(deck.id)}> Delete </button>
            </div>
            ))
        }
    </>
  );
}


export default Home;
